import React from 'react';
import './App.css';
import Header from './components/common/header/Header';
import FeaturedImage from './components/featured-image/FeaturedImage';
import ImageGrid from './components/image-grid/ImageGrid';
import API from './services/DogGalleryApi';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      breeds: null,
      selectedBreeds: [],
      selectedBreedByDefault: null,
      selectedBreedByDefaultList: null,
      imag1: null
    };

    this.getListByBreed = this.getListByBreed.bind(this);
    this.handleSelectDog = this.handleSelectDog.bind(this);
  }

  getMostThreeBreeds(list) {
    let defaultList = ['poodle', 'chow', 'bulldog'];
    let selectedBreeds = [];

    Object.keys(list).map((item) => {
      for (let i = 0; i < defaultList.length; i++) {
        if (defaultList[i] === item) {
           return selectedBreeds.push(item);
        }
      }
    });

    this.setState({selectedBreeds: selectedBreeds});

    return selectedBreeds;
  }

  getSelectedBreed(breed) {
    API.get('/breed/' + breed + '/images').then(response => {
      this.setState({
        selectedBreedByDefault: response.data.message[0],
        selectedBreedByDefaultList: response.data.message,
        title: breed
      });
    })
    .catch(e => {
      console.log(e);
    });
  }

  getListByBreed(breed) {
    this.getSelectedBreed(breed);
  }

  handleSelectDog(id) {
    this.setState({
      selectedBreedByDefault: this.state.selectedBreedByDefaultList[id]
    })
  }

  componentDidMount() {

    API.get('/breeds/list/all').then(response => {
      this.setState({breeds: this.getMostThreeBreeds(response.data.message)});
      this.getSelectedBreed(this.state.selectedBreeds[0]);
    })
    .catch(e => {
      console.log(e);
    });
  }

  render() {
    return (
      <div className="App container-md">
        {this.state.breeds && 
          <Header title={this.state.title} breeds={this.state.breeds} action={this.getListByBreed} />
        }
        {this.state.selectedBreedByDefault && 
          <FeaturedImage selectedBreedByDefault={this.state.selectedBreedByDefault} />
        }
        {this.state.selectedBreedByDefaultList &&
          <ImageGrid selectedBreedByDefaultList={this.state.selectedBreedByDefaultList} handleClick={this.handleSelectDog}/>
        }
      </div>
    );
  }

}

export default App;
