import React from 'react';
import './Header.css';
import BreedSelect from '../../breed-select/BreedSelect';

class Header extends React.Component {

  capitalize(title) {
    return title.charAt(0).toUpperCase() + title.slice(1);
  }

  render() {
    return (
      <section className='inner-container header-wrapper'>
        <h1 className='header'><span class='title'>{this.props.title && this.capitalize(this.props.title)}</span> Dog Gallery</h1>
        <p className='sub-header'>ITMD-565 - vmanapragada@hawk.iit.edu</p>

        {this.props.breeds && <BreedSelect breeds={this.props.breeds} handleChange={this.props.action} />}
      </section>
    );
  }

}

export default Header;
